import { CommonModule } from '@angular/common';
import { HousingLocationsComponent } from '../housing-locations/housing-locations.component';
import { Housinglocation } from '../housinglocation';
import { ProductComponent } from '../product/product.component';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [
    CommonModule,
    HousingLocationsComponent,
    ProductComponent
  ],
  template: `
  <!-- Komponent home -->
    <section>
      <form action="">
        <input type="text">
        <button class="primary" type="button">Search</button>
      </form>
    </section>
    <section class="results">
      <app-housing-locations *ngFor="let Housinglocation of housingLocationList" [Housinglocation]="Housinglocation"]></app-housing-locations>
    </section>
    <section class="listing-products">
      <!-- <app-product [Product]="product"></app-product> -->
    </section>
  `,
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  readonly baseUri = 'https://res.cloudinary.com/dj484tw6k/f_auto,q_auto,c_pad,b_white,w_505,h_505/v1650447482/b5d46983-5b20-4e42-867e-ad9ccd7b2de2.jpg';

